sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
	c.selectedIndex = null;
	c.selectItem = function(index) {
		var id = c.data.notes[index].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
		c.selectedIndex = index;
	};

	$rootScope.$on("updateList", function(event, data) {
		console.log("data.id=" + data.noteID);
		if (c.selectedIndex != null) {
			console.log("c.selectedIndex=" + c.selectedIndex)
			c.data.notes[c.selectedIndex].title = data.noteTitle;
			c.data.notes[c.selectedIndex].note = data.noteBody.slice(0,20);
		}
	});
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var noteGR = new GlideRecord("x_snc_createnotes_note");
	noteGR.addQuery("user", gs.getUser().getID() );
	noteGR.orderByDesc("sys_created_on");
	noteGR.query();
	while (noteGR.next() ) {
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		noteObj.note = noteGR.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}


})();
